import { Vector } from "~/types";

export class Paddle {
  private paddleImage: HTMLImageElement = new Image();
  private moveLeft: boolean;
  private moveRight: boolean;
  private moveUp: boolean;
  private moveDown:  boolean;
  
  constructor(
    private speed: number,
    private paddleWidth: number,
    private paddleHeight: number,
    private position: Vector,
    image: string
  ) {
    this.speed = speed;
    this.paddleWidth = paddleWidth;
    this.paddleHeight = paddleHeight;
    this.position = position;
    this.moveLeft = false;
    this.moveRight = false;
    this.moveDown = false;
    this.moveUp = false;
    this.paddleImage.src = image;

    // Event Listeners
    document.addEventListener('keydown', this.handleKeyRight);
    document.addEventListener('keyup', this.handleKeyLeft);


  }

  // Getters
  get width(): number {
    return this.paddleWidth;
  }

  get height(): number {
    return this.paddleHeight;
  }

  get pos(): Vector {
    return this.position;
  }

  get image(): HTMLImageElement {
    return this.paddleImage;
  }

  get isMovingLeft(): boolean {
    return this.moveLeft;
  }

  get isMovingRight(): boolean {
    return this.moveRight;
  }

  get isMovingUp(): boolean {
    return this.moveUp;
  }

  get isMovingDown(): boolean {
    return this.moveDown;
  }

  movePaddle(): void {
    if(this.moveLeft) this.pos.x -= this.speed;
    if(this.moveRight) this.pos.x += this.speed;
    if(this.moveDown) this.pos.y -= this.speed;
    if(this.moveUp) this.pos.y += this.speed;
  }

  
  handleKeyLeft = (e: KeyboardEvent): void => {

    if (e.code === 'ArrowLeft' || e.key === 'ArrowLeft') this.moveLeft = false;
    if (e.code === 'ArrowRight' || e.key === 'ArrowRight')
      this.moveRight = false;

    if (e.code === 'ArrowUp' || e.key === 'ArrowUp') this.moveDown = false;
    if (e.code === 'ArrowDown' || e.key === 'ArrowDown')
      this.moveUp = false;

  };

  handleKeyRight = (e: KeyboardEvent): void => {

    if (e.code === 'ArrowLeft' || e.key === 'ArrowLeft') this.moveLeft = true;
    if (e.code === 'ArrowRight' || e.key === 'ArrowRight')
      this.moveRight = true;
    
    if (e.code === 'ArrowUp' || e.key === 'ArrowUp') this.moveDown = true;
    if (e.code === 'ArrowDown' || e.key === 'ArrowDown')
      this.moveUp = true;

  };

}
